import React from "react";
// reactstrap components
import {
  Card,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
  CarouselItem,
  CarouselIndicators,
} from "reactstrap";

// core components
// javascript library that creates a parrallax effect
import Rellax from "rellax";
// reactstrap components
import { Container, Button, Link } from 'react-floating-action-button'
// core components
import ScrollTransparentNavbar from "components/Navbars/ScrollTransparentNavbar.js";
import BlogPostsHeader from "components/Headers/BlogPostsHeader.js";
import FooterBlack from "components/Footers/FooterBlack.js";

// sections for this page
import Components from "./presentation-sections/Components.js";
import NucleoIcons from "./index-sections/NucleoIcons.js";
import Carousel from "./index-sections/Carousel.js";
import BasicComponents from "./presentation-sections/BasicComponents.js";
import Cards from "./presentation-sections/Cards.js";
import Content from "./presentation-sections/Content.js";
import Sections from "./presentation-sections/Sections.js";
import Examples from "./presentation-sections/Examples.js";
import FreeDemo from "./presentation-sections/FreeDemo.js";
import Icons from "./presentation-sections/Icons.js";
import Image from "./presentation-sections/Image.js";
import Testimonials from "./presentation-sections/Testimonials.js";
import Pricing from "./presentation-sections/Pricing.js";
import PlainCards from "./index-sections/PlainCards.js";

function Testimonials2() {
  React.useEffect(() => {
    document.body.classList.add("presentation-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;

    // initialise Rellax for this page
    if (window.innerWidth >= 991) {
      setTimeout(function () {
        new Rellax(".rellax", {
          center: true,
        });
      }, 5000);
      new Rellax(".rellax-header");
      new Rellax(".rellax-text");
    }


    // we need to add a script for the github buttons
    let script = document.createElement("script");
    script.src = "https://buttons.github.io/buttons.js";
    script.id = "github-buttons-script-id";
    document.body.appendChild(script);

    return function cleanup() {
      document.body.classList.remove("presentation-page");
      document.body.classList.remove("sidebar-collapse");

      // we need to remove the script when we change the page
      script.parentNode.removeChild(script);
    };
  });
  return (
    <>
    <div
      className="section section-testimonials"
      data-background-color="black"
    >
      <Row>
        <Col className="ml-auto mr-auto text-center" md="8">
          <h2 className="title">Trusted by 800+ People</h2>
          <h5 className="description">
            Our work is confirmed to be of the highest quality by <b>800+ people</b> in over{" "}
            <b>5 cities</b>. This is what some of them think:
          </h5>
        </Col>
      </Row>
      <Row>
        <Col md="2">
          <div className="testimonials-people">
            <img
              alt="..."
              className="left-first-person img-raised rellax"
              data-rellax-speed="1"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/calebogden/128.jpg"
            ></img>
            <img
              alt="..."
              className="left-second-person img-raised rellax"
              data-rellax-speed="3"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/mlane/128.jpg"
            ></img>
            <img
              alt="..."
              className="left-third-person img-raised rellax"
              data-rellax-speed="2"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/jsa/128.jpg"
            ></img>
            <img
              alt="..."
              className="left-fourth-person img-raised rellax"
              data-rellax-speed="5"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/kerem/128.jpg"
            ></img>
            <img
              alt="..."
              className="left-fifth-person img-raised rellax"
              data-rellax-speed="7"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/adhamdannaway/128.jpg"
            ></img>
          </div>
        </Col>
        <Col md="8">
          <Carousel activeIndex={activeIndex} next={next} previous={previous}>
            <CarouselIndicators
              items={items}
              activeIndex={activeIndex}
              onClickHandler={goToIndex}
            />
            {items.map((item, key) => {
              return (
                <CarouselItem
                  onExiting={onExiting}
                  onExited={onExited}
                  key={key}
                >
                  {item.content}
                </CarouselItem>
              );
            })}
            <a
              className="left carousel-control carousel-control-prev"
              data-slide="prev"
              href="#pablo"
              onClick={(e) => {
                e.preventDefault();
                previous();
              }}
              role="button"
            >
              <span className="now-ui-icons arrows-1_minimal-left" />
              <span className="sr-only">Previous</span>
            </a>
            <a
              className="right carousel-control carousel-control-next"
              data-slide="next"
              href="#pablo"
              onClick={(e) => {
                e.preventDefault();
                next();
              }}
              role="button"
            >
              <span className="now-ui-icons arrows-1_minimal-right" />
              <span className="sr-only">Next</span>
            </a>
          </Carousel>
        </Col>
        <Col md="2">
          <div className="testimonials-people">
            <img
              alt="..."
              className="right-first-person img-raised rellax"
              data-rellax-speed="5"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/tonypeterson/128.jpg"
            ></img>
            <img
              alt="..."
              className="right-second-person img-raised rellax"
              data-rellax-speed="1"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/dancounsell/128.jpg"
            ></img>
            <img
              alt="..."
              className="right-fourth-person img-raised rellax"
              data-rellax-speed="7"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/mattsince87/128.jpg"
            ></img>
            <img
              alt="..."
              className="right-fifth-person img-raised rellax"
              data-rellax-speed="3"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/rem/128.jpg"
            ></img>
            <img
              alt="..."
              className="right-sixth-person img-raised rellax"
              data-rellax-speed="5"
              src="https://s3.amazonaws.com/uifaces/faces/twitter/chadengle/128.jpg"
            ></img>
          </div>
        </Col>
      </Row>
    </div>
    </>
    );
    }

export default Testimonials2;
