import React from "react";
// javascript library that creates a parrallax effect
import Rellax from "rellax";
// reactstrap components
import { Container, Button, Link } from 'react-floating-action-button'
// core components
import WhiteNavbar from "components/Navbars/WhiteNavbar.js";
import BlogPostHeader from "components/Headers/BlogPostHeader.js";
import FooterBlack from "components/Footers/FooterBlack.js";

// sections for this page

import Testimonials from "./presentation-sections/Testimonials.js";


function Testimonials2() {
  React.useEffect(() => {
    document.body.classList.add("Testimonials2-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;

    // initialise Rellax for this page
    if (window.innerWidth >= 991) {
      setTimeout(function () {
        new Rellax(".rellax", {
          center: true,
        });
      }, 5000);
      new Rellax(".rellax-header");
      new Rellax(".rellax-text");
    }


    // we need to add a script for the github buttons
    let script = document.createElement("script");
    script.src = "https://buttons.github.io/buttons.js";
    script.id = "github-buttons-script-id";
    document.body.appendChild(script);

    return function cleanup() {
      document.body.classList.remove("Testimonials2-page");
      document.body.classList.remove("sidebar-collapse");

      // we need to remove the script when we change the page
      script.parentNode.removeChild(script);
    };
  });
  return (
    <>

    <WhiteNavbar />


      <div className="wrapper">
<BlogPostHeader/>
<Testimonials />

<Container>
            <Link href="/contact-us"
                tooltip="E-Mail"
                icon="far fa-sticky-note" />
            <Link href="/contact-us"
                tooltip="Call Us"
                icon="fas fa-user-plus" />
            <Button
                tooltip="Get a quote!"
                icon="fas fa-plus"
                rotate={true}
                onClick={() => alert('Contact us here!')} />
        </Container>
            <FooterBlack />

      </div>
    </>
  );
}

export default Testimonials2;
